$(document).ready(function() {
    // hack for cache bug
    var content = $('pre code.language-mermaid').html();
    if(content != null)
    {   
        $('pre').replaceWith(function(){return $('<div class="mermaid" />').append(content);});
    }
    // end hack
    mermaid.initialize({startOnLoad:true});
    
   mermaid.ganttConfig = {axisFormatter: [['%d.%m.%Y', function (d){return d.getDay() == 1;}]]};});